e();            if (sel == 1) classe = '.todos';            if (sel == 2) classe = '.clube';            if (sel == 3) classe = '.licenca';            if (sel == 4) classe = '.indicado';            if (sel == 5) classe = '.usuario';            if (sel == 6) classe = '.sistema';            $(classe).show();            var qtd = $(classe).length;            $('#qtd').html(qtd);        }    </script>@endsection